#!/usr/bin/env python3

DNA = ''

upper_DNA = DNA.upper()

print ("Number of A's in DNA sequence:", upper_DNA.count("A"))

print ("Number of T's in DNA sequence:", upper_DNA.count("G"))
print ("Number of G's in DNA sequence:", upper_DNA.count("T"))
print ("Number of C's in DNA sequence:", upper_DNA.count("C"))

#replace T with U
DNA = ""

print ("Initial DNA sequence:", DNA)

RNA = DNA.replace("T", "U")

print ("Predicted RNA sequence:",RNA)

#replace T with U mixed cases
DNA = ""

print ("Initial DNA sequence:", DNA)

RNA1 = DNA.replace("T", "U")

RNA2 = RNA1.replace("t", "u")

print ("Predicted RNA sequence:",RNA2)

#calculating AT content

DNA = ""

a_content = DNA.count("A")
t_content = DNA.count("T")

totalDNAlength = len(DNA)

print ("Total AT content:", ((a_content + t_content)/totalDNAlength)*100)

g_content = DNA.count("G")
c_content = DNA.count("C")

print ("Total GC content:", ((g_content + c_content)/totalDNAlength)*100)

#extracting substring

DNA = ""

print ("DNA substring from nucleotide 100-200:", DNA[99:200])

print ("Number of G's in substring:", DNA[99:200].count("G"))

#extracting new case-sensitive substring

DNA ="" 

print ("DNA substring from nucleotide 100-200:", DNA[99:200])

print ("Number of G's in substring:", DNA[99:200].count("G")+DNA[99:200].count("g"))

#complement and reverse complement of DNA string

origSeq = "ATGCAGGGGAAACATGATTCAGGAC"

comp_A = origSeq.replace("A","t")
comp_AT = comp_A.replace("T","a")
comp_ATG = comp_AT.replace("G","c")
comp_ATGC = comp_ATG.replace("C","g")

print ("Original sequence:", origSeq)

print ("Complement sequence:", comp_ATGC.upper())

print ("Reverse complement sequence:", comp_ATGC.upper()[::-1])

#finding EcoRI site

DNA = ""

startEcoRI = DNA.find("GAATTC") + 1
endEcoRI = startEcoRI + 5

print ("EcoRI startPos: {} endPos: {}".format(startEcoRI,endEcoRI))
